                                console.log(copy);
                                setOrder(copy);
                            }}>Delete Item</button>
                        </div>
                    ))}
                    <div>
                        <button onClick={(e) => submitOrder()}>Submit Order</button>
                    </div>
                </Offcanvas.Body>
            </Offcanvas>

            <div className='buttonNavigation'>
                <button className='buttonNavigation__button' onClick={(e) => setMenuFilter("Main")}>Main</button>
                <button className='buttonNavigation__button' onClick={(e) => setMenuFilter("Deserts")} > Deserts</button>
                <button className='buttonNavigation__button' onClick={(e) => setMenuFilter("Drinks")} > Drinks</button >
            </div >

            <div className='orderItems'>
                {menu && menu.map((item) => (

                    <div className='orderItem' key={item.id}>
                        <img src="" alt="Pancakes" />
                        <h4>{item.name}</h4>
                        <p>{item.description}</p>
                        {/* <input type="number" defaultValue="1" min="1" max="99"></input> */}
