                <label for="name">Name:</label>
                <input type="text" name="name" placeholder="Food Name" value={name} required
                    onChange={(e) => setName(e.target.value)}
                ></input>


                <label for="description">Description:</label>
                <textarea name="description" placeholder="List of ingredients and the dishes characteristics" value={itemCreate.description} required
                    onChange={(e) => setItemCreate({ ...itemCreate, description: e.target.value })}
                ></textarea>

                <label for="price">Single Unit Price:</label>
                <input type="number" value={itemCreate.price} required min="0"
                    onChange={(e) => setItemCreate({ ...itemCreate, price: e.target.value })}
                ></input>

                <button>Create Menu Item</button>
            </form >

            <div className='orderItems'>
                {menu.map((item) => (
                    <div className='orderItem' key={item.id}>
                        <img src="" alt="Pancakes" />
                        <h4>{item.name}</h4>
