import { useState } from "react";
import { useEffect } from "react";

function MenuEditAdmin() {

    const [menu, SetMenu] = useState({});

    const itemCreate = {
        name: "",
        description: "",
        price: null
    }



    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(itemCreate);
    }

    useEffect(() => {

    }, [])

    return (
        <>
            <form className="menuAddForm" onSubmit={handleSubmit}>
                {/* <label for="picture">Picture:</label>
        <input type="file" required></input> */}

                <label for="name">Name:</label>
                <input type="text" name="name" placeholder="Food Name" required
                    onChange={(e) => itemCreate.name = e.target.value}
                ></input>


                <label for="description">Description:</label>
                <textarea name="description" placeholder="List of ingredients and the dishes characteristics" required
                    onChange={(e) => itemCreate.description = e.target.value}
                ></textarea>

                <label for="price">Single Unit Price:</label>
                <input type="number" required min="0"
                    onChange={(e) => itemCreate.price = e.target.value}
                ></input>

                <button>Create Menu Item</button>
            </form>

            <div className='orderItems'>
                <div className='orderItem'>
                    <img src="" alt="Pancakes" />
                    <h4>Pancakes</h4>
                    <p>Double glazed with butter, mega packed with juicy jam
                        with a monster load of whipping cream. 4 of these in 1 serving!
                    </p>
                    <input type="number"></input>
                    <button>Add to order</button>
                </div>

                <div className='orderItem'>
                    <img src="" alt="Pancakes" />
                    <h4>Pancakes</h4>
                    <p>Double glazed with butter, mega packed with juicy jam
                        with a monster load of whipping cream. 4 of these in 1 serving!
                    </p>
                    <input type="number"></input>
                    <button>Add to order</button>
                </div>

                <div className='orderItem'>
                    <img src="" alt="Pancakes" />
                    <h4>Pancakes</h4>
                    <p>Double glazed with butter, mega packed with juicy jam
                        with a monster load of whipping cream. 4 of these in 1 serving!
                    </p>
                    <input type="number"></input>
                    <button>Add to order</button>
                </div>
            </div>
        </>
    );
}

export default MenuEditAdmin;
